nasal.load:


print("setting RJSS rwy27 sequenced flashing lights system.");
var loop_id = 0;
var flash_count = 29;
var timer_magnification = 1;
var next_light = 1/60;

	var loop1 = func(id)
	{

		var delta_time = getprop("/sim/time/delta-realtime-sec");
		var fps = getprop("/sim/frame-rate");

		if(fps <= 60)
		{
		timer_magnification = 60/fps;
		next_light = delta_time;
		}

		var flash_num = math.round(flash_count);

		setprop("/scenery/RJSS/RJSS_r27_app_flash/light-number", flash_num);

		flash_count -= timer_magnification;

		if (flash_num < 1)
		{
			flash_count = 29;
		};
		timer1.restart(next_light);
	}

	var flag1 = getprop("/scenery/RJSS/RJSS_r27_app_flash/running");
	if(flag1 == nil or flag1 == 0){
		var timer1 = maketimer(next_light, func { loop1(id) });
		timer1.singleShot =1;
		setprop("/scenery/RJSS/RJSS_r27_app_flash/running",1);
		timer1.start();
		}

nasal.unload:

print("Unload RJSS rwy27 sequenced flashing lights system.");
timer1.stop();
setprop("/scenery/RJSS/RJSS_r27_app_flash/running",0);
props.globals.getNode("/scenery").removeChildren("RJSS");
